import com.vordel.kps.impl.KPS;
def invoke(msg){
try{      
def userTable = KPS.getInstance().getModel().getAliases().get("subscribe");
def updatelist = '';
def servicename = msg.get("service");
def ipRow = userTable.getCached(servicename);
def sublist = ipRow.get("subscriptionKey");

if(sublist.size() > 0) {
	return false;
}
else {
	return true;
}

return false; 
}catch(e){
	msg.put("scripterror",e);
	return false;
}
}

